mapper.select:
SELECT ROW_NUMBER() OVER (ORDER BY r.REVIEW_ID) AS NUM, p.PRODUCT_ID, r.REVIEW_ID, o.ORDER_NUMBER, r.REVIEW_STATUS, p.PRODUCT_NAME, r.REVIEW_CONTENTS, u.USER_EMAIL, u.USER_NAME, r.REVIEW_CREATED_AT FROM review r JOIN product p ON r.PRODUCT_ID = p.PRODUCT_ID JOIN user u ON r.USER_ID_NO = u.USER_ID_NO JOIN orders o ON r.ORDER_ID = o.ORDER_ID
AND r.REVIEW_STATUS = #{status}
AND r.REVIEW_CREATED_AT >= DATE_SUB(CURDATE(), INTERVAL #{duration} DAY)
AND r.REVIEW_CREATED_AT BETWEEN TIMESTAMP(#{startDate}, '00:00:00') AND TIMESTAMP(#{endDate}, '23:59:59')
AND p.PRODUCT_NAME LIKE CONCAT('%', #{searchText}, '%')
AND u.USER_NAME = #{searchText}
